mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name as category_name from dish d
left join category c on d.category_id = c.id


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and d.status = #{status}


order by update_time desc

select count(id) from dish


        and id in

            #{id}



        and status = #{status}
